```typescript
class ImageService {
  baseUrl = process.env.NEXT_PUBLIC_BASE_URL;

  private buildUrl(endpoint: string): string {
    if (!this.baseUrl) {
      throw new Error(
        "Base URL not found. Please, set the BASE_URL environment variable.",
      );
    }
    return `${this.baseUrl}${endpoint}`;
  }

  async getImages(): Promise<ImageClass[]> {
    try {
      const response = await fetch(this.buildUrl('/images'));
      if (!response.ok) {
        throw new Error(`Failed to fetch images: ${response.statusText}`);
      }
      return response.json();
    } catch (error) {
      if (error instanceof Error) {
        console.error("Error fetching images:", error.message);
      } else {
        console.error("An unknown error occurred while fetching images.");
      }
      throw error;
    }
  }
}

export const useImageService = () => {
  return React.useMemo(() => new ImageService(), []);
};
```

