class ImageService {
  baseUrl = process.env["BASE_URL"];

  async getImages(): Promise<ImageClass[]> {
    if (!this.baseUrl) {
      throw new Error("Base URL is not defined");
    }

    try {
      const response = await fetch(`${this.baseUrl}/images`);
