```typescript
import { useMemo } from "react";

// Dentro do seu componente GalleryPage
const imagensFiltradas = useMemo(() => {
  // Exemplo: Filtrar imagens com base em alguma condição
  return images.filter(image => image.size > 1000); // Condição de exemplo
